#INSIDE THE EC2 INSTANCE EXECUTE:#Install and start Docker on AWS EC2sudo yum update -ysudo yum install docker -ysudo usermod -a -G docker ec2-user#INSTALL DOCKER COMPOSE:# https://docs.docker.com/compose/install/# 1 Run this command to download the current stable release of Docker Compose:sudo curl -L "https://github.com/docker/compose/releases/download/1.29.2/docker-compose-$(uname -s)-$(uname -m)" -o /usr/local/bin/docker-compose# 2 Apply executable permissions to the binary:sudo chmod +x /usr/local/bin/docker-compose# Got permission denied while trying to connect to the Docker daemon socket at unix:///var/run/docker.sock: Get "http://%2Fvar%2Frun%2Fdocker.sock/v1.24/containers/json": dial unix /var/run/docker.sock: connect: permission deniedsudo chmod 666 /var/run/docker.socksudo service docker start